import React, { useContext, useState } from "react";
import { BrowserRouter as Router, NavLink } from "react-router-dom";

import { UserContext } from "./App.js";
import "./Header.scss";

const Header = ({ setRedirect, checkToken }) => {
  const userData = useContext(UserContext);
  const [hamburgerOpen, setHamburgerOpen] = useState(false);
  const menu = document.getElementById("menu");
  if (userData && userData.userName === "") {
    checkToken();
  }

  const openMenu = () => {
    if (hamburgerOpen) menu.style.left = "50%";
    else menu.style.left = 0;
    setHamburgerOpen(!hamburgerOpen);
  };

  return (
    <>
      <div className="header">
        <div className="logo">
          <h1>A Musical Planet</h1>
        </div>
        <div
          className={"hamburger" + (hamburgerOpen ? " open" : "")}
          onClick={() => openMenu}
        >
          <span></span>
          <span></span>
          <span></span>
          <span></span>
        </div>
        <ul id="menu" className={!hamburgerOpen ? " hidden" : ""}>
          <NavLink
            className="linkButton"
            to="/maps"
            activeStyle={{ backgroundColor: "white" }}
          >
            Maps
          </NavLink>
          <NavLink
            className="linkButton"
            to="/about"
            activeStyle={{ backgroundColor: "white" }}
          >
            About
          </NavLink>
          <NavLink
            className="linkButton"
            to="/leaderboard"
            activeStyle={{ backgroundColor: "white" }}
          >
            Leaderboard
          </NavLink>
          {userData && userData.displayName ? (
            <NavLink
              className="linkButton"
              to={`/user/${userData.userName}`}
              activeStyle={{ backgroundColor: "white" }}
            >
              {userData.displayName}
            </NavLink>
          ) : (
            <></>
          )}
        </ul>
        <div className="navbar">
          <NavLink
            className="linkButton"
            to="/maps"
            activeStyle={{ backgroundColor: "white" }}
          >
            Maps
          </NavLink>
          <NavLink
            className="linkButton"
            to="/about"
            activeStyle={{ backgroundColor: "white" }}
          >
            About
          </NavLink>
          <NavLink
            className="linkButton"
            to="/leaderboard"
            activeStyle={{ backgroundColor: "white" }}
          >
            Leaderboard
          </NavLink>
          {userData && userData.displayName ? (
            <NavLink className="userLink" to={`/user/${userData.userName}`}>
              <img src={userData.profilePicture} alt="" />
            </NavLink>
          ) : (
            <></>
          )}
        </div>
      </div>
    </>
  );
};

export default Header;
