import React, { memo } from "react";
import {
  ZoomableGroup,
  ComposableMap,
  Geographies,
  Geography,
} from "react-simple-maps";
import { geoMercator } from "d3-geo";

const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const rounded = (num) => {
  if (num > 1000000000) {
    return Math.round(num / 100000000) / 10 + "Bn";
  } else if (num > 1000000) {
    return Math.round(num / 100000) / 10 + "M";
  } else {
    return Math.round(num / 100) / 10 + "K";
  }
};

const MapChart = (props) => {
  const projection = geoMercator().scale(153).center([40, 50]);
  return (
    <>
      <ComposableMap
        data-tip=""
        projection={projection}
        style={{
          width: "100%",
          height: "auto",
          outline: "none",
          stroke: "#000000",
          strokeWidth: 0.5,
        }}
      >
        <ZoomableGroup
          translateExtent={[
            [-100, -50],
            [860, 600],
          ]}
        >
          <Geographies geography={geoUrl}>
            {({ geographies }) =>
              geographies.map((geo) => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  onClick={() => {
                    props.setCurrChosen(geo.properties.NAME);
                  }}
                  onMouseEnter={() => {
                    props.setTooltipContent(geo.properties.NAME);
                  }}
                  onMouseLeave={() => {
                    props.setTooltipContent("");
                  }}
                  style={{
                    default: {
                      fill: "#D6D6DA",
                      stroke: "#000000",
                      strokeWidth: 0.1,
                      outline: "none",
                    },
                    hover: {
                      fill: "#F53",
                      stroke: "#000000",
                      strokeWidth: 0.1,
                      outline: "none",
                    },
                    pressed: {
                      fill: "#E42",
                      stroke: "#000000",
                      strokeWidth: 0.1,
                      outline: "none",
                    },
                  }}
                />
              ))
            }
          </Geographies>
        </ZoomableGroup>
      </ComposableMap>
    </>
  );
};

export default memo(MapChart);
