import React, { useState, useEffect } from "react";
import axios from "axios";
import MapData from "./MapData.json";
import LeaderboardUser from "./LeaderboardUser";
import "./Leaderboard.scss";

const Leaderboard = () => {
  const [leaderboardStats, setLeaderboardStats] = useState([]);
  const [chosenMap, setChosenMap] = useState("world");
  const [currPage, setCurrPage] = useState(0);

  useEffect(() => {
    axios(`http://localhost:8888/getLeaderboard`, {
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      method: "GET",
    }).then((leaderboardData) => {
      console.log("LEADERBOARD:");
      console.log(leaderboardData);
      setLeaderboardStats(leaderboardData.data);
    });
  }, []);

  return (
    <div className="leaderboardContainer">
      <div className="leaderboardHead">
        <h1>Leaderboard</h1>
        <select
          id="maplist"
          onChange={(e) => {
            setChosenMap(
              e.target.value[0].toLowerCase() +
                e.target.value.slice(1).replace(" ", "")
            );
          }}
        >
          {MapData.map((map) => (
            <option key={map.name} value={map.name}>
              {map.name}
            </option>
          ))}
        </select>
      </div>
      <div className="leaderboard">
        <button onClick={() => setCurrPage(currPage - 1)}>Right</button>
        <div className="leaderboardUsers">
          {leaderboardStats[chosenMap] ? (
            leaderboardStats[chosenMap]
              .slice(currPage * 5, currPage * 5 + 5)
              .map((userData, rank) => (
                <LeaderboardUser
                  key={userData.userName + chosenMap + (rank + 1)}
                  rank={rank + 1}
                  userData={userData}
                  map={chosenMap}
                />
              ))
          ) : (
            <></>
          )}
        </div>
        <button onClick={() => setCurrPage(currPage + 1)}>Right</button>
      </div>
    </div>
  );
};

export default Leaderboard;
