import React from "react";

const AudioPlayer = ({ audioRef, trackURL }) => {
  return (
    <>
      <audio id="audioPlayer" controls ref={audioRef}>
        <source
          volume="2"
          allow="autoplay"
          src={trackURL}
          type="audio/mpeg"
        ></source>
      </audio>
      <div className="customAudio">
        <button
          onClick={() => {
            return audioRef.current.play();
          }}
        >
          {audioRef.current.paused ? "Play" : "Pause"}
        </button>
      </div>
    </>
  );
};

export default AudioPlayer;
