import React from "react";
import {
  interaction,
  layer,
  custom,
  control, //name spaces
  Interactions,
  Overlays,
  Controls, //group
  Map,
  Layers,
  Overlay,
  Util, //objects
} from "react-openlayers";

const MapPage = () => {
  const showPopup = () => {
    console.log("popup!");
  };

  var iconFeature = new ol.Feature(new ol.geom.Point([0, 0]));
  var source = new ol.source.Vector({ features: [iconFeature] });
  var marker = new custom.style.MarkerStyle(
    "https://openlayers.org/en/v4.0.1/examples/data/icon.png"
  );
  // var marker = new custom.style.MarkerStyle(
  //   "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.pinclipart.com%2Fpindetail%2FiiToRJi_map-marker-icon-location-icon-png-clipart%2F&psig=AOvVaw15OEpctuur2aGwZ7djFOzl&ust=1627329195164000&source=images&cd=vfe&ved=0CAsQjRxqFwoTCOC95ef__vECFQAAAAAdAAAAABAD"
  // );
  // let selectedMarkerStyle = Util.cloneObject(marker.style);
  // selectedMarkerStyle.getImage().setOpacity(1);
  return (
    <>
      <Map view={{ center: [0, 0], zoom: 2 }} onClick={showPopup}>
        <Layers>
          <layer.Tile />
          <layer.Vector source={source} style={marker.style} zIndex="1" />
        </Layers>
        <Overlays>
          <Overlay ref={(comp) => (this.overlayComp = comp)} element="#popup" />
        </Overlays>
        <Controls attribution={false} zoom={true}>
          <control.Rotate />
          <control.ScaleLine />
          <control.FullScreen />
          <control.OverviewMap />
          <control.ZoomSlider />
