import React, { useState, useEffect, useRef } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
} from "react-router-dom";

const Play = (props) => {
  console.log("acctok: ", props.accessToken);
  const [currArtist, setCurrArtist] = useState("");
  const [currTrack, setCurrTrack] = useState("");
  const [redirect, setRedirect] = useState(false);

  const audioRef = useRef(null);
  //props.spotifyApi.setAccessToken(props.token);
  console.log("tok: ", props.accessToken);

  const getNewArtist = () => {
    if (props.accessToken === null) {
      console.log("REDIRECTING");
      setRedirect(true);
    }
    fetch("https://api.spotify.com/v1/playlists/0s2RYa25RwXR5rDAMcpB3P", {
      headers: { Authorization: "Bearer " + props.accessToken },
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        let track = "";
        let artist = "";
        while (track === "") {
          const chosenIndex = Math.floor(
            Math.random() * data.tracks.items.length
          );
          const possibleTrack =
            data.tracks.items[chosenIndex].track.preview_url;
          if (possibleTrack != null) {
            track = possibleTrack;
            artist = data.tracks.items[chosenIndex].track.artists[0].name;
          }
        }
        setCurrArtist(artist);
        setCurrTrack(track);
        audioRef.current.load();
      });
  };

  useEffect(() => {
    getNewArtist();
  }, []);

  if (redirect) return <Redirect to="/" />;
  return (
    <>
      <h1>Artist: {currArtist}</h1>
      <audio controls id="music" ref={audioRef}>
        <source
          volume="2"
          allow="autoplay"
          src={currTrack}
          type="audio/mpeg"
        ></source>
        {/* Your browser does not support the audio element. */}
      </audio>
      <div>
        <button onClick={getNewArtist}>New Song</button>
      </div>
    </>
  );
};

export default Play;
