import React, { useState } from "react";
import "./Play";

const AudioPlayer = ({ audioRef, trackURL, useState }) => {
  const [paused, setPaused] = useState(true);
  const togglePause = () => {
    if (paused === true) audioRef.current.play();
    else audioRef.current.pause();
    setPaused(!paused);
  };

  return (
    <>
      <audio id="audioPlayer" controls ref={audioRef}>
        <source
          volume="2"
          allow="autoplay"
          src={trackURL}
          type="audio/mpeg"
        ></source>
      </audio>
      <div className="customAudio">
        <button
          className={
            !audioRef.current || audioRef.current.paused
              ? "box center pause"
              : "box center"
          }
          onClick={() => togglePause()}
        >
          {!audioRef.current || audioRef.current.paused ? "Play" : "Pause"}
        </button>
      </div>
    </>
  );
};

export default AudioPlayer;
