import React, { useState, useEffect } from "react";
import axios from "axios";

const Leaderboard = () => {
  const [leaderboardStats, setLeaderboardStats] = useState([]);
  const [chosenMap, setChosenMap] = useState("world");

  useEffect(() => {
    axios(`http://localhost:8888/getLeaderboard`, {
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      method: "GET",
    }).then((leaderboardData) => {
      console.log("LEADERBOARD:");
      console.log(leaderboardData);
      setLeaderboardStats(leaderboardData.data);
    });
  }, []);

  return (
    <div>
      <h1>LEADERBOARD</h1>
      <datalist id="map">
        {this.state.data.map((item, key) => (
          <option key={key} value={item.displayValue} />
        ))}
      </datalist>
      <ul>
        {leaderboardStats[chosenMap] ? (
          leaderboardStats[chosenMap].map((user) => (
            <li>
              {user.userName}: {user.maxScores[chosenMap]}
            </li>
          ))
        ) : (
          <></>
        )}
      </ul>
    </div>
  );
};

export default Leaderboard;
