import React from "react";

const AudioPlayer = ({ audioRef, trackURL, useEffect }) => {
  return (
    <>
      <audio id="audioPlayer" controls ref={audioRef}>
        <source
          volume="2"
          allow="autoplay"
          src={trackURL}
          type="audio/mpeg"
        ></source>
      </audio>
      <div className="customAudio">
        <button
          onClick={() => {
            e.target.classList.toggle("pause");
            audioRef.current.paused
              ? audioRef.current.play()
              : audioRef.current.pause();
          }}
        >
          {!audioRef.current || audioRef.current.paused ? "Play" : "Pause"}
        </button>
      </div>
    </>
  );
};

export default AudioPlayer;
