//import "./App.css";
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
} from "react-router-dom";

import axios from "axios";
import { Credentials } from "./Credentials";
import Auth from "./Auth";
import Play from "./Play";
import queryString from "query-string";
const SpotifyWebApi = require("spotify-web-api-node");

function App() {
  const [token, setToken] = useState("");

  const gotToken = (newToken) => {
    console.log(`Setting token to ${newToken}`);
    spotifyApi.setAccessToken(newToken);
    console.log("CURR TOKEN: ", spotifyApi.getAccessToken());
    setToken(newToken);
  };

  var spotifyApi = new SpotifyWebApi(Credentials());

  const handleLogin = () => {
    // window.location.replace(
    //   spotifyApi.createAuthorizeURL(
    //     ["user-read-private", "user-read-email"],
    //     "123"
    //   )
    // );
    window.location.replace("http://localhost:8888/login");
  };

  const handleAuth = () => {
    console.log("here");
    console.log(queryString.parse(window.location.search.access_token));
  };
  return (
    <Router>
      <Switch>
        <Route path="/auth/">
          <Auth setTokenFunc={() => handleAuth} />
        </Route>
        <Route exact path="/">
          <button type="submit" onClick={handleLogin}>
            Login
          </button>
          <h1>Testing this!</h1>
        </Route>
        <Route exact path="/relogin">
          {() => handleLogin}
        </Route>
        <Route path="/play">
          <Play spotifyApi={spotifyApi} token={token} />
        </Route>
        <Route path="/error">
          <h1>Error! Check console</h1>
        </Route>
        <Route path="*">
          <h1>Not Found!</h1>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
