import React, { useEffect } from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
} from "react-router-dom";

const Play = (props) => {
  const [currArtist, setCurrArtist] = useState("");
  useEffect(() => {
    if (props.spotifyApi.getAccessToken === null) return <Redirect to="/" />;

    props.spotifyApi.setAccessToken(props.token);
    console.log("My curr API token is ", props.spotifyApi.getAccessToken());
    props.spotifyApi.getPlaylist("0s2RYa25RwXR5rDAMcpB3P").then(
      function (data) {
        //console.log('Track: ' + JSON.stringify(data.body));
        let track = "";
        let artist = "";
        while (track === "") {
          const chosenIndex = Math.floor(
            Math.random() * data.body.tracks.items.length
          );
          const possibleTrack =
            data.body.tracks.items[chosenIndex].track.preview_url;
          if (possibleTrack != null) {
            track = possibleTrack;
            artist = data.body.tracks.items[chosenIndex].track.artists[0].name;
          }
        }
        //const chosenIndex = Math.floor(Math.random() * data.body.tracks.items.length);
        //console.log('URL: ' + data.body.tracks.items[0].preview_url);
        //track = data.body.tracks.items[chosenIndex].track.preview_url;
        //console.log(track);
        setCurrArtist(artist);
      },
      function (err) {
        console.log("Something went wrong!", err);
        return <Redirect to="/error" />;
      }
    );
  }, []);

  return <h1>{currArtist}</h1>;
};

export default Play;
