import React, { useState } from "react";
import MapItem from "./MapItem";
import "./ChooseMap.scss";
import MapData from "./MapData.json";

const ChooseMap = ({ handleMapChosen }) => {
  const [chosenMap, setChosenMap] = useState("world");
  return (
    <div className="ChooseMapContainer">
      <div className="MapHead">
        <h1>Official Maps</h1>
      </div>
      <div className="MapItemsDesktop">
        {MapData.map((currMap) => (
          <MapItem
            key={currMap.name}
            map={currMap}
            handleMapChosen={handleMapChosen}
          />
        ))}
      </div>
      <div className="MapItemsMobile">
        <select
          id="maplist"
          onChange={(e) => {
            setChosenMap(
              e.target.value[0].toLowerCase() +
                e.target.value.slice(1).replace(" ", "")
            );
          }}
        >
          {MapData.map((map) => (
            <option key={map.name} value={map.name}>
              {map.name}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default ChooseMap;
